import React, { Component, useState } from 'react';
import { Key } from './key' // 引入 API key
import GoogleMapReact from 'google-map-react';
// Default Dublin center
// 53.347816
// -6.259317
const MyPositionMarker = ({ text }) => <div>{text}</div>;

// Map
const SimpleMap = (props) => {
  const [myPosition, setMyPosition] = useState({
    lat: 53.347816,
    lng: -6.259317
  })

  const [mapApiLoaded, setMapApiLoaded] = useState(false)
  const [mapInstance, setMapInstance] = useState(null)
  const [mapApi, setMapApi] = useState(null)

  const apiHasLoaded = (map, maps) => {
    setMapInstance(map)
    setMapApi(maps)
    setMapApiLoaded(true)
  };

  const handleCenterChange = ({bound, zoom, center}) => {
    if(mapApiLoaded) {
      setMyPosition({
        // center.lat()  center.lng() catch the center in the screen
        lat: mapInstance.center.lat(),
        lng: mapInstance.center.lng()
      })
    }
  }
  // Find bus stop
  const findBusStopLocation = () => {
    if(mapApiLoaded) {
      const service = new mapApi.places.PlacesService(mapInstance)

      const request = {
        location: myPosition,
        radius: 1000,
        type: ['bus stop']
      };

      service.nearbySearch(request, (results, status) => {
        if(status === mapApi.places.PlacesServiceStatus.OK) {
          console.log(results)
        }
      })
    }
  }

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <input type="button" value="show bus stop" onClick={findBusStopLocation} />
      <GoogleMapReact
        bootstrapURLKeys={{
          key: Key,
          libraries:['places'] // 要在這邊放入我們要使用的 API
        }}
        onChange={handleCenterChange}
        defaultCenter={props.center}
        defaultZoom={props.zoom}
        yesIWantToUseGoogleMapApiInternals
        onGoogleApiLoaded={({ map, maps }) => apiHasLoaded(map, maps)}
      >
        <MyPositionMarker
          lat={myPosition.lat}
          lng={myPosition.lng}
          text="My Position"
        />
      </GoogleMapReact>
    </div>
  );
}

// Set defaultProps
SimpleMap.defaultProps = {
  center: {
    lat: 53.348358, 
    lng: -6.260327
  },
  zoom: 17
};


// App
function App() {
  return (
    <div className="App">
      <SimpleMap />
    </div>
  );
}

export default App;
