import React, { Component, useState, useEffect, useRef } from 'react';
import { debounce } from 'lodash' //Autocomplete time delay setting
import { Key } from './key' // API key
import GoogleMapReact from 'google-map-react';

// Default Dublin center
// 53.347816
// -6.259317
const cityCenter = []; 
cityCenter.push({
    lat:   "keyName",
    value: "the value"
});


const MyPositionMarker = ({ text }) => <div>{text}</div>;

const Marker = ({ icon, text }) => (
  <div>
    <img style={{ height: '30px', width: '30px' }} alt="icon" src={icon} />
    <div>{text}</div>
  </div>
)


// Map
const SimpleMap = (props) => {
  const [myPosition, setMyPosition] = useState({
    lat: 53.347816,
    lng: -6.259317
  })
  const [currentCenter, setCurrentCenter] = useState({
    lat: 53.347816,
    lng: -6.259317
  })
  // Default state
  let inputRef = useRef(null);
  const [inputText, setInputText] = useState('')
  const [searchType, setSearchType] = useState(['bus_station'])
  const [mapApiLoaded, setMapApiLoaded] = useState(false)
  const [mapInstance, setMapInstance] = useState(null)
  const [mapApi, setMapApi] = useState(null)
  const [places, setPlaces] = useState([])
  const [mapType, setMapType] = useState('roadmap')
  const [autocompleteResults, setAutocompleteResults] = useState([])


  const apiHasLoaded = (map, maps) => {
    setMapInstance(map)
    setMapApi(maps)
    setMapApiLoaded(true)
  };

  // Find bus stop
  const findLocation = () => {
    if(mapApiLoaded) {
      const service = new mapApi.places.PlacesService(mapInstance)

      const request = {
        location: myPosition,
        radius: 1000,
        type: searchType
      };

      service.nearbySearch(request, (results, status) => {
        if(status === mapApi.places.PlacesServiceStatus.OK) {
          setPlaces(results)
        }
      })
    }
  }

  useEffect(() => {
    findLocation()
  }, [searchType, myPosition, mapApiLoaded])

  const handleCenterChange = ({bound, zoom, center}) => {
    if(mapApiLoaded) {
      setMyPosition({
        // center.lat()  center.lng() catch the center in the screen
        lat: mapInstance.center.lat(),
        lng: mapInstance.center.lng()
      })
    }
  }

  const SearchType = ({ text, type }) => {
    return <input type="button" value={text} name={type} />
  }

  const handleInput = () =>{
    setInputText(inputRef.current.value)
  }

  const handleSearchType = e => {
    setSearchType(e.target.name)
  }

  // Change map type
  const handleMapTypeId = e => {
    setMapType(e.target.name)
  }

  const handleAutocomplete = ()  =>{
    if (mapApiLoaded) {
      const service = new mapApi.places.AutocompleteService()
      const request ={
        input: inputText
      }

      service.getPlacePredictions(request, (results, status, ) => {
        if(status === mapApi.places.PlacesServiceStatus.OK){
          setAutocompleteResults(results)
        }
      });
    }
  }
  useEffect(()=>{
    handleAutocomplete()
  }, [inputText])

  const handleClickToChangeMyPosition = e => {
    const placeId = e.target.getAttribute('dataid')
    const service = new mapApi.places.PlacesService(mapInstance)
    const request = {
      placeId: placeId,
      fields: [
        'geometry'
      ]
    }
    service.getDetails(request, (result, status)=>{
      if( status === mapApi.places.PlacesServiceStatus.OK) {
        const newPosition = {
          lat: result.geometry.location.lat(),
          lng: result.geometry.location.lng()
        }
        setCurrentCenter(newPosition) // 改變地圖視角位置
        setMyPosition(newPosition) // 改變 MyPosition
        setAutocompleteResults([]) // 清空自動搜尋地址清單
        inputRef.current.value = '' // 清空 <input>
      }
    })
  }




  return (
      <div style={{ height: '100vh', width: '100%' }}>
      <div>
        <div >
        To: <input ref={inputRef} type="text" onChange={ debounce(handleInput, 500) } />
        </div>
        <div onClick={handleClickToChangeMyPosition}> 
          {(autocompleteResults && inputText) &&
          autocompleteResults.map(item=>(
            <div key={item.id} dataid={item.place_id} id="search-list">
              {item.description}
            </div>
          ))}
        </div>
      </div>

      <div onClick={handleSearchType}>
        <SearchType text="bus stop" type="bus_station" />
        <SearchType text="somewhere fun" type="tourist_attraction" />
      </div>
      <input type="button" value="satellite" onClick={ handleMapTypeId } name="hybrid" />
      <input type="button" value="road" onClick={ handleMapTypeId } name="roadmap" />
      <GoogleMapReact
        bootstrapURLKeys={{
          key: Key,
          libraries:['places'] // Our api
        }}
        //hacky way to fix bug
        distanceToMouse={()=>{}}

        center={currentCenter}
        options={{ mapTypeId: mapType }}
        onChange={handleCenterChange}
        defaultCenter={props.center}
        defaultZoom={props.zoom}
        yesIWantToUseGoogleMapApiInternals
        //map: map itself, maps: google map object 
        onGoogleApiLoaded={({ map, maps }) => apiHasLoaded(map, maps)}

      >
        <MyPositionMarker
          lat={myPosition.lat}
          lng={myPosition.lng}
          text="My Position"
        />
        {places.map(item=>(
          <Marker
            icon={item.icon}
            key={item.id}
            lat={item.geometry.location.lat()}
            lng={item.geometry.location.lng()}
            text={item.name}
            placeId={item.place_id}
          />
        ))}
      </GoogleMapReact>
    </div>
  );
}

// Set defaultProps
SimpleMap.defaultProps = {
  center: {
    lat: 53.348358, 
    lng: -6.260327
  },
  zoom: 17
};


// App
function App() {
  return (
    <div className="App">
      <SimpleMap />
    </div>
  );
}

export default App;
